using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Task13
{
    class Program
    {
        static void Main(string[] args)
        {
            string t = "";
            string b = Console.ReadLine();
            int l = b.Length;
            if (l != 1)
            {
                Console.WriteLine("Нужно ввести ровно один символ");
                return;
            }
            if (!t.Contains(b))
            {
                Console.WriteLine();
                return;
            }
            int index = t.IndexOf(b);
            int index1 = t.LastIndexOf(b);
            int p;//позиция символа после запятой
            int count = 1;
            Console.Write(index + " ");
            while (index < index1)
            {
                p = index + 1;
                index = t.IndexOf(b, p);
                count++;
                Console.Write(index + " ");
            }
        }
    }
}
